'use strict';

module.exports = {
  register: function() {
    sw.client.on('connect', function(err) {
      if (err) throw err;

      sw.client.get('/me', function(data) {
        if (data) {
          sw.profile.setData(data);
          sw.emit('ready');
        }
      });
    });
    return true;
  },
  unregister: function() {
    sw.client.off('connect');
    return true;
  }
};
