'use strict';

let connectBtn = $('[data-action="sw.client.connect"]'),
  callback = function(jqEvent) {
    jqEvent.preventDefault();

    sw.display.setMainTitle('Connecting to SoundCloud....');
    sw.display.setSubTitle('Authenticating...');

    sw.client.connect();
  };

module.exports = {
  register: function() {
    if (connectBtn.length === 0) {
      return false;
    }
    connectBtn.on('click', callback);
    return true;
  },
  unregister: function() {
    if (connectBtn.length === 0) {
      return false;
    }
    connectBtn.off('click', callback);
    return true;
  }
};
