'use strict';
/**
 * Viewport
 */

let props = Object.defineProperties,
  EventEmitter = require('events'),
  inherits = require('util').inherits;

function Viewport() {
  props(this, {
    _content: {
      configurable: false,
      enumerable: false,
      writable: true,
      value: null
    }
  })

  EventEmitter.call(this);
}

inherits(Viewport, EventEmitter);

Viewport.prototype.setContent = function(content) {
  this._content = content;
  this.emit('update-content', {
    viewport: this,
    content: content
  });
};

Viewport.prototype.getContent = function() {
  return this._content;
};

module.exports = Viewport;
