'use strict';
/**
 * Toggle sections
 */

let toggle = require('utils/toggle');

module.exports = {
  init: function() {
    $('#section-switch .btn').on('click', function(jqEvent) {
      jqEvent.preventDefault();

      $('[data-section]').addClass('hidden');
      toggle.toggle($('[data-section="' + $(this).find('input:radio').val() + '"]'), 'hidden');
    });
  }
};
