'use strict';
/**
 * Toggle states
 */

function __enable_state(el, state) {
  if (el && el.length > 0) {
    el.addClass(state);
  }
}

function __disable_state(el, state) {
  if (el && el.length > 0) {
    el.removeClass(state);
  }
}

function __has_state(el, state) {
  if (el && el.length > 0) {
    return el.hasClass(state);
  }
  return false;
}

module.exports = {
  toggle: function(el, state, cb1, cb2) {
    if (__has_state(el, state)) {
      __disable_state(el, state);
      if (typeof cb1 == 'function') {
        cb1.call(el, state, el);
      }
    } else {
      __enable_state(el, state);
      if (typeof cb2 == 'function') {
        cb2.call(el, state, el);
      }
    }
    return el;
  },
  enable: __enable_state,
  disable: __disable_state,
  has: __has_state
};
